var assert = require('tapsert');
var License = require('./');
var now = new Date();

var perpetual = new License({
  product: '*', features: ['*'],
  activationDate: new Date(0),
  expirationDate: new Date('2525-01-01'), // "if man is still alive"
});

var negative = new License();

assert(perpetual.covers('prod', 'feature', now),
       'perpetual license covers prod.feature now');

assert(perpetual.covers(),
       'perpetual is a generally valid license');

assert(!negative.covers('*', '*', now),
       'empty license does not cover anything');

assert(negative.covers(),
       'empty license is still a license');

